require "test_helper"
require "minitest/autorun"

class ChatResponderTest < ActiveSupport::TestCase
  setup do
    ChatResponder.class_eval do
      # we're testing the private get_response method directly
      # because the public call method is stubbed globally
      public :get_response
    end
  end

  test "it builds messages using chat history and the 3 most relevant chapters and blobs and then calls OpenAI chat api" do
    story = users(:one).stories.create(title: "story title", summary: "story summary")
    chapter1 = story.chapters.create(title: "chapter one", body: "chapter one body")
    chapter1.embedding = embedding_starting_with(0.00005)
    chapter1.save
    chapter2 = story.chapters.create(title: "chapter two", body: "chapter two body")
    chapter2.embedding = embedding_starting_with(0.00004)
    chapter2.save
    chapter3 = story.chapters.create(title: "chapter three", body: "chapter three body")
    chapter3.embedding = embedding_starting_with(0.00003)
    chapter3.save
    chapter4 = story.chapters.create(title: "chapter four", body: "chapter four body")
    chapter4.embedding = embedding_starting_with(0.00002)
    chapter4.save
    chapter5 = story.chapters.create(title: "chapter five", body: "chapter five body")
    chapter5.embedding = embedding_starting_with(0.00001)
    chapter5.save
    blob1 = story.blobs.create(title: "blob one", body: "blob one body")
    blob1.embedding = embedding_starting_with(0.00005)
    blob1.save
    blob2 = story.blobs.create(title: "blob two", body: "blob two body")
    blob2.embedding = embedding_starting_with(0.00004)
    blob2.save
    blob3 = story.blobs.create(title: "blob three", body: "blob three body")
    blob3.embedding = embedding_starting_with(0.00003)
    blob3.save
    blob4 = story.blobs.create(title: "blob four", body: "blob four body")
    blob4.embedding = embedding_starting_with(0.00002)
    blob4.save
    blob5 = story.blobs.create(title: "blob five", body: "blob five body")
    blob5.embedding = embedding_starting_with(0.00001)
    blob5.save

    chat = chapter4.chats.create(user: users(:one))
    chat_message1 = chat.chat_messages.create(user: users(:one), message: "hello bot")
    chat_message2 = chat.chat_messages.create(user: users(:system), message: "hello human")
    chat_message3 = chat.chat_messages.create(user: users(:one), message: "how are you?")

    # question_embedding is [0.00001, 0.00001, ...] (set in global stub for EmbeddingCreator)

    # Looking for most relevant chapters from 1-4
    # expected ordered relevant chapters is 4->3->2

    # Looking for most relevant blobs
    # expected ordered relevant blobs is 5->4->3

    api_mock = Minitest::Mock.new
    api_mock.expect(:chat, {"choices"=>[{"message"=>{"content"=>"test response"}}]}, parameters: {
      model: "gpt-3.5-turbo",
        messages: [
        {role: "system", content: "Answer the questions based on the story below, if the question can't be answered, say 'I dont know'.\n\n========================================================\n\nHere is the story:\n\nchapter four body chapter three body chapter two body\n\n========================================================\n\nHere is extra information about the story:\n\nblob five body blob four body blob three body"},
        {role: "user", content: "hello bot"},
        {role: "assistant", content: "hello human"},
        {role: "user", content: "how are you?"},
      ],
        max_tokens: 189
    })

    OpenAI::Client.stub :new, api_mock do
      ret = ChatResponder.new(chat_message3).get_response
      assert_equal "test response", ret
    end

  end

